package com.movie.controller;import com.movie.entity.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;@RestControllerpublic class MovieController {    @Autowired    private RestTemplate restTemplate;    @GetMapping("/user/{id}")    public User getById(@PathVariable Long id) {        return restTemplate.getForObject("http://localhost:8080/user/" + id, User.class);    }}